document.addEventListener("readystatechange",e=>{if("complete"===e.target.readyState&&document.querySelector("body").classList.contains("desktop")){const s=document.createElement("div");s.classList.add("cursor"),document.body.appendChild(s),document.addEventListener("mousemove",function(e){var t=e.clientX,e=e.clientY;s.style.left=t+"px",s.style.top=e+"px"}),document.querySelectorAll(".wp-site-blocks a").forEach(e=>{e.addEventListener("mouseover",()=>{s.classList.add("hover")}),e.addEventListener("mouseleave",()=>{s.classList.remove("hover")})})}});
